// FaceDetection using MediaPipe via CDN

export class FaceDetectionSystem {
  constructor(onDetectionUpdate, onReady) {
    this.onDetectionUpdate = onDetectionUpdate;
    this.onReady = onReady;

    this.lastBlinkTime = 0;
    this.earHistory = [];
    this.blinkCount = 0;
    this.yawnCount = 0;
    this.lastYawnTime = 0;
    this.eyeOpenTime = 0;
    this.stressScore = 0;
    this.gazeOffScreen = 0;

    this.faceMesh = null;
    this.camera = null;
    this.isInitialized = false;
    this.stream = null;
    this.isRunning = false;
    this.healthCheckInterval = null;
    this.isReconnecting = false; // Flag para prevenir reconexões múltiplas

    // NOTA: Não iniciar health check aqui - será iniciado após a câmera estar rodando
  }

  startHealthCheck() {
    this.healthCheckInterval = setInterval(async () => {
      // Não fazer nada se já estiver reconectando ou não estiver rodando
      if (this.isReconnecting || !this.isRunning) {
        return;
      }

      const videoElement = document.getElementById('input_video');

      // Verificar se o elemento existe e tem stream
      if (!videoElement || !videoElement.srcObject) {
        console.warn('Stream de vídeo não encontrado');
        return;
      }

      // Verificar estado do vídeo de forma mais tolerante
      const tracks = videoElement.srcObject.getVideoTracks();
      if (tracks.length === 0 || tracks[0].readyState === 'ended') {
        console.warn('Track de vídeo foi finalizada, tentando reconectar...');

        // Prevenir múltiplas reconexões simultâneas
        if (this.isReconnecting) {
          return;
        }

        this.isReconnecting = true;
        try {
          // Parar tudo antes de reconectar
          const wasRunning = this.isRunning;
          await this.stop();

          // Aguardar um pouco antes de reconectar
          await new Promise(resolve => setTimeout(resolve, 2000));

          // Se estava rodando antes, reconectar
          if (wasRunning) {
            await this.start();
          }
        } catch (error) {
          console.error('Falha ao reconectar câmera:', error);
          this.isRunning = false;
        } finally {
          this.isReconnecting = false;
        }
      } else if (videoElement.readyState < 2) {
        // readyState < 2 significa que o vídeo ainda está carregando
        // Isso é normal, não precisa reconectar
        console.log('Vídeo ainda carregando, readyState:', videoElement.readyState);
      }
    }, 10000); // Verificar a cada 10 segundos (menos agressivo)
  }

  async initialize() {
    if (this.isInitialized) return;

    try {
      console.log('Iniciando carregamento do MediaPipe...');

      // Verificar suporte do navegador
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Este navegador não suporta acesso à câmera');
      }

      // Carregar MediaPipe dinamicamente se ainda não estiver carregado
      if (typeof window === 'undefined' || !window.FaceMesh || !window.Camera) {
        console.log('Carregando MediaPipe dinamicamente...');

        // Configurar Module antes de carregar
        if (typeof window !== 'undefined') {
          window.Module = window.Module || {};
          if (window.Module.hasOwnProperty('arguments')) {
            delete window.Module.arguments;
          }
        }

        // Carregar scripts do MediaPipe
        const scripts = [
          'https://cdn.jsdelivr.net/npm/@mediapipe/camera_utils@0.3.1632432234/camera_utils.js',
          'https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh@0.4.1633559619/face_mesh.js'
        ];

        // Tentar carregar scripts um por um
        for (const src of scripts) {
          try {
            await this.loadScript(src);
            // Aguardar um pouco entre carregamentos
            await new Promise(resolve => setTimeout(resolve, 500));
          } catch (error) {
            console.warn(`Falha ao carregar ${src}, tentando alternativa...`);
            // Tentar sem versão específica
            const fallbackSrc = src.replace(/@[\d.]+/g, '');
            try {
              await this.loadScript(fallbackSrc);
              await new Promise(resolve => setTimeout(resolve, 500));
            } catch (fallbackError) {
              console.error(`Falha ao carregar alternativa: ${fallbackSrc}`);
              throw new Error(`Não foi possível carregar MediaPipe: ${src}`);
            }
          }
        }

        // Aguardar até que o MediaPipe esteja disponível
        let maxWaitAttempts = 30; // 3 segundos máximo (30 * 100ms)
        let waitAttempt = 0;

        while (typeof window === 'undefined' || !window.FaceMesh || !window.Camera) {
          if (waitAttempt >= maxWaitAttempts) {
            throw new Error('MediaPipe não foi carregado após tentativas.');
          }
          await new Promise(resolve => setTimeout(resolve, 100));
          waitAttempt++;
        }
      }

      console.log('MediaPipe está disponível');

      // Aguardar um pouco mais para garantir que o MediaPipe está completamente pronto
      await new Promise(resolve => setTimeout(resolve, 500));

      console.log('Configurando FaceMesh...');

      // Configurar FaceMesh com opções padrão
      // Não especificar locateFile para usar o padrão do MediaPipe
      const faceMeshOptions = {
        maxNumFaces: 1,
        refineLandmarks: true,
        minDetectionConfidence: 0.5,
        minTrackingConfidence: 0.5
      };

      try {
        this.faceMesh = new window.FaceMesh(faceMeshOptions);
      } catch (error) {
        // Se falhar com Module.arguments, tentar com locateFile explícito
        if (error.message && error.message.includes('arguments')) {
          console.warn('Tentando inicializar FaceMesh com locateFile explícito...');
          faceMeshOptions.locateFile = (file) => {
            // Tentar usar o caminho relativo ao CDN
            if (file.includes('.wasm') || file.includes('.data')) {
              return `https://cdn.jsdelivr.net/npm/@mediapipe/face_mesh/${file}`;
            }
            return file;
          };
          this.faceMesh = new window.FaceMesh(faceMeshOptions);
        } else {
          throw error;
        }
      }

      // Aguardar um pouco antes de inicializar
      await new Promise(resolve => setTimeout(resolve, 300));

      try {
        await this.faceMesh.initialize();
      } catch (initError) {
        // Se a inicialização falhar com Module.arguments, tentar reinicializar
        if (initError.message && initError.message.includes('arguments')) {
          console.warn('Erro na inicialização, tentando novamente após limpeza...');

          // Limpar e tentar novamente
          if (this.faceMesh) {
            try {
              await this.faceMesh.close();
            } catch (e) {
              // Ignorar erros ao fechar
            }
          }

          // Aguardar antes de tentar novamente
          await new Promise(resolve => setTimeout(resolve, 1000));

          // Tentar criar uma nova instância
          this.faceMesh = new window.FaceMesh({
            maxNumFaces: 1,
            refineLandmarks: true,
            minDetectionConfidence: 0.5,
            minTrackingConfidence: 0.5
          });

          await new Promise(resolve => setTimeout(resolve, 500));
          await this.faceMesh.initialize();
        } else {
          throw initError;
        }
      }

      this.faceMesh.onResults((results) => {
        this.processResults(results);
      });

      console.log('FaceMesh configurado com sucesso');
      this.isInitialized = true;
    } catch (error) {
      console.error('Erro na inicialização:', error);
      throw error;
    }
  }

  loadScript(src) {
    return new Promise((resolve, reject) => {
      // Verificar se o script já foi carregado
      const existingScript = Array.from(document.querySelectorAll('script')).find(
        s => s.src === src || s.src.includes(src.split('/').pop())
      );

      if (existingScript && (window.FaceMesh || window.Camera)) {
        console.log('Script já carregado:', src);
        resolve();
        return;
      }

      const script = document.createElement('script');
      script.src = src;
      script.crossOrigin = 'anonymous';
      script.async = true;

      // Verificar se o conteúdo carregado é JavaScript válido
      script.onload = () => {
        // Verificar se não é uma página de erro HTML
        if (window.FaceMesh || window.Camera || src.includes('face_mesh') || src.includes('camera_utils')) {
          console.log('Script carregado com sucesso:', src);
          resolve();
        } else {
          // Aguardar um pouco mais antes de considerar erro
          setTimeout(() => {
            if (window.FaceMesh || window.Camera) {
              resolve();
            } else {
              reject(new Error(`Script ${src} não carregou corretamente (possível erro HTML)`));
            }
          }, 1000);
        }
      };

      script.onerror = (error) => {
        console.error('Erro ao carregar script:', src, error);
        reject(new Error(`Falha ao carregar ${src}. Verifique se a URL está correta e acessível.`));
      };

      document.head.appendChild(script);
    });
  }

  processResults(results) {
    if (!results.multiFaceLandmarks || results.multiFaceLandmarks.length === 0) {
      this.onDetectionUpdate({
        faceDetected: false,
        gazeOnScreen: false,
        fatigueScore: 0,
        blinkCount: this.blinkCount,
        yawnCount: this.yawnCount
      });
      return;
    }

    const landmarks = results.multiFaceLandmarks[0];
    const eyeAspectRatio = this.calculateEAR(landmarks);
    const yawnRatio = this.calculateYawnRatio(landmarks);
    const gazeDirection = this.estimateGaze(landmarks);

    // Detectar piscadas
    if (eyeAspectRatio < 0.2) {
      const currentTime = Date.now();
      if (currentTime - this.lastBlinkTime > 300) {
        this.blinkCount++;
        this.lastBlinkTime = currentTime;
      }
    }

    // Detectar bocejos
    if (yawnRatio > 0.6) {
      const currentTime = Date.now();
      if (currentTime - this.lastYawnTime > 2000) {
        this.yawnCount++;
        this.lastYawnTime = currentTime;
      }
    }

    // Calcular score de fadiga
    const fatigueScore = this.calculateFatigue(eyeAspectRatio, yawnRatio, this.blinkCount);

    // Detectar desvio de olhar
    const isGazeOnScreen = gazeDirection.x > -0.3 && gazeDirection.x < 0.3 && 
                           gazeDirection.y > -0.2 && gazeDirection.y < 0.2;

    if (!isGazeOnScreen) {
      this.gazeOffScreen++;
    }

    this.onDetectionUpdate({
      faceDetected: true,
      gazeOnScreen: isGazeOnScreen,
      fatigueScore: Math.min(fatigueScore, 1.0),
      blinkCount: this.blinkCount,
      yawnCount: this.yawnCount,
      attentionScore: isGazeOnScreen ? 1.0 : 0.0
    });
  }

  calculateEAR(landmarks) {
    try {
      const leftEAR = this.getEyeAspectRatio([
        landmarks[33], landmarks[133], landmarks[157], 
        landmarks[158], landmarks[159], landmarks[160]
      ]);

      const rightEAR = this.getEyeAspectRatio([
        landmarks[362], landmarks[385], landmarks[386],
        landmarks[387], landmarks[388], landmarks[466]
      ]);

      return (leftEAR + rightEAR) / 2;
    } catch (error) {
      console.warn('Erro ao calcular EAR:', error);
      return 0.3; // valor padrão seguro
    }
  }

  getEyeAspectRatio(eyePoints) {
    const verticalDist1 = this.distance(eyePoints[1], eyePoints[5]);
    const verticalDist2 = this.distance(eyePoints[2], eyePoints[4]);
    const horizontalDist = this.distance(eyePoints[0], eyePoints[3]);

    return (verticalDist1 + verticalDist2) / (2 * horizontalDist);
  }

  calculateYawnRatio(landmarks) {
    // Pontos da boca
    const mouthTop = landmarks[13];
    const mouthBottom = landmarks[14];

    const mouthOpening = Math.abs(mouthTop.y - mouthBottom.y);

    return mouthOpening;
  }

  estimateGaze(landmarks) {
    // Estimativa simples do gaze baseada na posição da íris
    const noseTip = landmarks[4];
    const leftEyeCenter = landmarks[33];
    const rightEyeCenter = landmarks[263];

    const gazeX = (leftEyeCenter.x + rightEyeCenter.x) / 2 - noseTip.x;
    const gazeY = noseTip.y - (leftEyeCenter.y + rightEyeCenter.y) / 2;

    return { x: gazeX, y: gazeY };
  }

  calculateFatigue(EAR, yawnRatio, blinkCount) {
    let fatigue = 0;

    // Fadiga por olhos fechados
    if (EAR < 0.2) fatigue += 0.4;
    else if (EAR < 0.25) fatigue += 0.2;

    // Fadiga por bocejo
    if (yawnRatio > 0.6) fatigue += 0.3;
    else if (yawnRatio > 0.4) fatigue += 0.1;

    // Fadiga por piscadas excessivas
    if (blinkCount > 20) fatigue += 0.3;

    return Math.min(fatigue, 1.0);
  }

  distance(point1, point2) {
    return Math.sqrt(
      Math.pow(point1.x - point2.x, 2) + 
      Math.pow(point1.y - point2.y, 2)
    );
  }

  async start() {
    try {
      if (this.isRunning) {
        console.log('Sistema já está em execução');
        return;
      }

      console.log('Iniciando sistema de detecção facial...');

      // Verificar disponibilidade de câmera
      const devices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = devices.filter(device => device.kind === 'videoinput');

      if (videoDevices.length === 0) {
        throw new Error('NotFoundError');
      }

      // Não precisamos obter a stream duas vezes - vamos apenas verificar permissões
      // A stream será obtida no loop abaixo
      console.log('Verificando disponibilidade da câmera...');

      // Inicializar MediaPipe
      await this.initialize();

      if (!this.faceMesh) {
        throw new Error('MediaPipe não inicializou corretamente');
      }

      const videoElement = document.getElementById('input_video');
      if (!videoElement) {
        throw new Error('Elemento de vídeo não encontrado');
      }

      // Configurar elemento de vídeo
      Object.assign(videoElement.style, {
        display: 'block',
        position: 'fixed',
        top: '0',
        left: '0',
        width: '1px',
        height: '1px',
        opacity: '0.01'
      });

      videoElement.muted = true;
      videoElement.playsInline = true;
      videoElement.crossOrigin = 'anonymous';

      if (!window.Camera) {
        throw new Error('MediaPipe Camera API não disponível');
      }

      console.log('Iniciando câmera com MediaPipe...');

      // Criar e configurar câmera com retry e timeout
      const maxRetries = 3;
      let cameraStarted = false;

      for (let i = 0; i < maxRetries && !cameraStarted; i++) {
        try {
          // Obter um stream de vídeo diretamente
          const stream = await navigator.mediaDevices.getUserMedia({
            video: {
              width: { ideal: 640 },
              height: { ideal: 480 },
              facingMode: "user",
              frameRate: { ideal: 30 }
            },
            audio: false
          });

          // Armazenar a stream antes de atribuir
          this.stream = stream;

          // Parar qualquer stream anterior se existir
          if (videoElement.srcObject) {
            const oldTracks = videoElement.srcObject.getVideoTracks();
            oldTracks.forEach(track => track.stop());
          }

          // Atribuir o stream ao elemento de vídeo
          videoElement.srcObject = stream;

          // Aguardar o vídeo estar pronto antes de tentar play
          await new Promise((resolve, reject) => {
            const timeout = setTimeout(() => {
              reject(new Error('Timeout ao aguardar vídeo carregar'));
            }, 5000);

            const onCanPlay = () => {
              clearTimeout(timeout);
              videoElement.removeEventListener('canplay', onCanPlay);
              resolve();
            };

            videoElement.addEventListener('canplay', onCanPlay, { once: true });
            videoElement.addEventListener('error', (e) => {
              clearTimeout(timeout);
              reject(new Error(`Erro ao carregar vídeo: ${videoElement.error?.message || 'Erro desconhecido'}`));
            }, { once: true });

            videoElement.load(); // Forçar carregamento
          });

          await videoElement.play();

          // Configurar a câmera do MediaPipe
          this.camera = new window.Camera(videoElement, {
            onFrame: async () => {
              if (this.faceMesh && videoElement.readyState === 4) {
                try {
                  await this.faceMesh.send({ image: videoElement });
                } catch (error) {
                  if (!error.message.includes('Canvas has been cleared')) {
                    console.warn('Erro no processamento do frame:', error);
                  }
                }
              }
            },
            width: 640,
            height: 480
          });

          // Iniciar o processamento da câmera com retry em caso de falha
          let startAttempts = 0;
          const maxStartAttempts = 3;

          while (startAttempts < maxStartAttempts) {
            try {
              await Promise.race([
                this.camera.start(),
                new Promise((_, reject) => 
                  setTimeout(() => reject(new Error('Timeout ao iniciar câmera')), 5000)
                )
              ]);
              cameraStarted = true;
              console.log('Câmera iniciada com sucesso');
              break;
            } catch (startError) {
              startAttempts++;
              console.warn(`Tentativa ${startAttempts} de iniciar câmera falhou:`, startError);
              if (startAttempts === maxStartAttempts) throw startError;
              await new Promise(resolve => setTimeout(resolve, 1000));
            }
          }
        } catch (error) {
          console.warn(`Tentativa ${i + 1} de ${maxRetries} falhou:`, error);

          // Limpar recursos em caso de erro
          if (this.camera) {
            try {
              this.camera.stop();
              this.camera = null;
            } catch (e) {
              console.warn('Erro ao parar câmera:', e);
            }
          }

          // Limpar stream em caso de erro
          if (videoElement.srcObject) {
            const tracks = videoElement.srcObject.getVideoTracks();
            tracks.forEach(track => track.stop());
            videoElement.srcObject = null;
            this.stream = null;
          }

          if (i === maxRetries - 1) {
            this.isRunning = false;
            throw error;
          }
          await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
        }
      }

      // Verificar se o vídeo está recebendo frames
      await new Promise((resolve, reject) => {
        const timeout = setTimeout(() => {
          reject(new Error('Timeout aguardando frames de vídeo'));
        }, 10000); // Aumentar timeout para 10 segundos

        const checkVideo = () => {
          if (videoElement.videoWidth > 0 && videoElement.videoHeight > 0) {
            clearTimeout(timeout);
            resolve();
          } else {
            requestAnimationFrame(checkVideo);
          }
        };

        // Verificar imediatamente e também aguardar eventos
        if (videoElement.videoWidth > 0 && videoElement.videoHeight > 0) {
          clearTimeout(timeout);
          resolve();
        } else {
          videoElement.addEventListener('playing', checkVideo, { once: true });
          videoElement.addEventListener('loadedmetadata', checkVideo, { once: true });
          videoElement.addEventListener('error', (e) => {
            clearTimeout(timeout);
            reject(new Error(`Erro no elemento de vídeo: ${videoElement.error?.message || 'Erro desconhecido'}`));
          }, { once: true });
        }
      });

      // IMPORTANTE: Marcar como rodando APÓS tudo estar funcionando
      this.isRunning = true;

      // Armazenar a stream atual
      this.stream = videoElement.srcObject;

      // Iniciar health check apenas após a câmera estar funcionando
      if (!this.healthCheckInterval) {
        this.startHealthCheck();
      }

      if (this.onReady) {
        this.onReady();
      }
    } catch (error) {
      console.error('Erro ao iniciar câmera:', error);

      // Informar o usuário sobre o problema específico
      let userMessage = 'Erro ao iniciar o sistema de monitoramento: ';
      if (error.name === 'NotAllowedError' || error.message.includes('Permission denied')) {
        userMessage += 'Permissão da câmera negada. Por favor, permita o acesso à câmera e recarregue a página.';
      } else if (error.name === 'NotFoundError' || error.message.includes('Requested device not found')) {
        userMessage += 'Nenhuma câmera encontrada. Conecte uma câmera e recarregue a página.';
      } else if (error.name === 'NotReadableError' || error.message.includes('Could not start video source')) {
        userMessage += 'Câmera pode estar em uso por outro aplicativo. Feche outros programas que possam estar usando a câmera.';
      } else if (error.name === 'NotSupportedError') {
        userMessage += 'Sua câmera não é compatível com os requisitos necessários. Tente usar outra câmera.';
      } else if (error.message.includes('Timeout')) {
        userMessage += 'Tempo excedido ao tentar acessar a câmera. Tente recarregar a página.';
      } else {
        userMessage += error.message || 'Erro desconhecido ao acessar a câmera.';
      }

      console.warn(userMessage);

      // Limpar recursos em caso de erro
      this.isRunning = false;
      this.isReconnecting = false;
      if (this.stream) {
        const tracks = this.stream.getVideoTracks();
        tracks.forEach(track => track.stop());
        this.stream = null;
      }

      // Limpar elemento de vídeo
      const videoElement = document.getElementById('input_video');
      if (videoElement && videoElement.srcObject) {
        const tracks = videoElement.srcObject.getVideoTracks();
        tracks.forEach(track => track.stop());
        videoElement.srcObject = null;
      }

      throw error;  // Vamos deixar o StudentView lidar com o erro
    }
  }

  simulateDetection() {
    console.log('Iniciando modo simulado de detecção facial');
    // Modo simulado para desenvolvimento sem câmera
    this.simulationInterval = setInterval(() => {
      const isGazeOnScreen = Math.random() > 0.3; // 70% chance de olhar para tela
      const fatigueScore = Math.random() * 0.8; // Score de fadiga variável
      const blinkIncrement = Math.floor(Math.random() * 3); // 0-2 piscadas por intervalo

      this.blinkCount += blinkIncrement;

      this.onDetectionUpdate({
        faceDetected: true,
        gazeOnScreen: isGazeOnScreen,
        fatigueScore: fatigueScore,
        blinkCount: this.blinkCount,
        yawnCount: this.yawnCount,
        attentionScore: isGazeOnScreen ? (1.0 - fatigueScore * 0.5) : 0.0
      });
    }, 2000);
  }

  async stop() {
    try {
      // Parar health check
      if (this.healthCheckInterval) {
        clearInterval(this.healthCheckInterval);
        this.healthCheckInterval = null;
      }

      // Parar câmera do MediaPipe
      if (this.camera) {
        try {
          await this.camera.stop();
        } catch (error) {
          console.warn('Erro ao parar câmera MediaPipe:', error);
        }
        this.camera = null;
      }

      // Fechar FaceMesh
      if (this.faceMesh) {
        try {
          await this.faceMesh.close();
        } catch (error) {
          console.warn('Erro ao fechar FaceMesh:', error);
        }
        this.faceMesh = null;
      }

      this.isInitialized = false;
      this.isRunning = false;
      this.isReconnecting = false;

      // Parar todas as tracks do stream
      if (this.stream) {
        const tracks = this.stream.getVideoTracks();
        tracks.forEach(track => {
          track.stop();
        });
        this.stream = null;
      }

      // Limpar o elemento de vídeo
      const videoElement = document.getElementById('input_video');
      if (videoElement) {
        if (videoElement.srcObject) {
          const tracks = videoElement.srcObject.getVideoTracks();
          tracks.forEach(track => track.stop());
        }
        videoElement.srcObject = null;
        videoElement.load();
      }

      console.log('Sistema de detecção facial parado com sucesso');
    } catch (error) {
      console.error('Erro ao parar o sistema:', error);
      this.isRunning = false;
    }
  }

  resetCounters() {
    this.blinkCount = 0;
    this.yawnCount = 0;
    this.gazeOffScreen = 0;
  }
}
